INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`)
VALUES
	(1,1,'Starter Kit','&lt;p&gt;Includes:&lt;/p&gt;&lt;p&gt;2 pcs. Evod battery (650 mAh)&lt;/p&gt;&lt;p&gt;2 pcs. Evod Clearomiser (1.6 ml capacity)&lt;/p&gt;&lt;p&gt;5 pcs. Evod Coil Heads (Resistance: 2.2 ohm)&lt;/p&gt;&lt;p&gt;1 USB charger&lt;/p&gt;&lt;p&gt;1 US/EU Wall Adaptor&lt;/p&gt;&lt;p&gt;1 User Manual&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;New to vaping? This starter kit is perfect for new and returning vapers! This starter kit is a must-try! It will give you the general feeling of advanced e-cigarettes without the cost.&amp;nbsp;&lt;/p&gt;&lt;p&gt;The device is extremely user-friendly and all necessary steps are included in the manual.&lt;/p&gt;','starter kit, evod, kanger evod starter kit, kanger au, starter, e-cig starter, e-cigarette starter kit','kevin','',''),
	(2,1,'Kanger Evod Mega Starter Kit','&lt;p&gt;Includes:&lt;/p&gt;&lt;p&gt;1 Evod Mega Battery (1900 mAh)&lt;/p&gt;&lt;p&gt;1 Evod Mega Clearomiser (1.5 ohm, 2.5 ml capacity)&lt;/p&gt;&lt;p&gt;1 Micro USB charger lead&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;No time to keep charging your battery? Try Kangertech\'s new starter kit which has a 1900 mAh battery. Not only will your battery last longer, the clearomiser can also hold more e-juice!&lt;/p&gt;&lt;p&gt;Works as simple as Kangertech\'s Evod Starter Kit!&lt;/p&gt;','evod mega, starter kit, starter kit, evod mega, kanger evod mega starter kit, kanger au, starter, e-cig starter, e-cigarette starter kit','kevin','',''),
	(3,1,'Kanger EMOW Starter Kit','&lt;p&gt;Includes:&lt;/p&gt;&lt;p&gt;1 EMOW VV Battery (1300 mAh)&lt;/p&gt;&lt;p&gt;1 Aerotank Emow Clearomiser (1.8 ml capacity)&lt;/p&gt;&lt;p&gt;5 pcs dual coil (1.5 ohm)&lt;/p&gt;&lt;p&gt;1 USB Cable&lt;/p&gt;&lt;p&gt;1 US &amp;nbsp;wall adapter&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Trying out different flavours? Taste the difference by using Kangertech\'s variable voltage starter kit! All you need to add is your juice and you can start vaping!&amp;nbsp; &amp;nbsp;This is a genuine product of Kangertech.&lt;/p&gt;','starter kit, emow, kanger emow starter kit, kanger au, starter, e-cig starter, e-cigarette starter kit, variable voltage, vv','kevin','',''),
	(4,1,'Aspire K1 Starter Kit','&lt;p&gt;Includes:&lt;/p&gt;&lt;p&gt;1 Aspire CF G-Power Battery (900 mAh)&lt;/p&gt;&lt;p&gt;1 Aspire K1 Glassomizer (1.5 ml capacity)&lt;/p&gt;&lt;p&gt;5 BVC Replacement Coils (Resistance: 1.6 ohm)&lt;/p&gt;&lt;p&gt;1 USB Charger&lt;/p&gt;&lt;p&gt;1 A/C Adaptor&lt;/p&gt;&lt;p&gt;1 User Manual&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Looking for something sleek? Seek no more! The Aspire K1 Starter Kit comes with everything you need to start vaping! This kit also utilises BVC (Bottom Vertical Coil) technology. No more dry hits! Let gravity do the work for you. Its metallic finish and the pyrex glass gives this kit the edge - simple yet elegant.&amp;nbsp;&lt;/p&gt;','','kevin','',''),
	(5,1,'Aspire Premium Kit','&lt;p&gt;Includes:&lt;/p&gt;&lt;p&gt;1 Aspire CF VV+ Battery (1000 mAh)&lt;/p&gt;&lt;p&gt;1 Nautilus Mini (2ml capacity)&lt;/p&gt;&lt;p&gt;5 BVC Replacement Coils&lt;/p&gt;&lt;p&gt;1 USB Charger&lt;/p&gt;&lt;p&gt;1 A/C Adaptor&lt;/p&gt;&lt;p&gt;1 User Manual&lt;/p&gt;&lt;p&gt;Planning on trying different e-juices? Find the best voltage for each of your juice with the Aspire Premium Kit. Variable Voltage (VV) allows you to increase or decrease your voltage depending on the thickness of your juice. Be careful though, extreme voltages may damage your coil.&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Please check the following link for proper voltages.&lt;/p&gt;','starter kit, premium, aspire premium starter kit, aspire au, starter, e-cig starter, e-cigarette starter kit, variable voltage, vv','kevin','',''),
	(6,1,'Kanger SUBOX Mini Starter Kit','&lt;p&gt;Includes:&lt;/p&gt;&lt;p&gt;1 KBOX Mini (18650 battery sold separately)&lt;/p&gt;&lt;p&gt;1 Subtank Mini (4.5 ml capacity)&lt;/p&gt;&lt;p&gt;1 OCC (0.5 ohm resistance)&lt;/p&gt;&lt;p&gt;1 OCC (1.5 ohm resistance)&lt;/p&gt;&lt;p&gt;2 RBA coils with PEEK insulator&lt;/p&gt;&lt;p&gt;1 RBA mini base&lt;/p&gt;&lt;p&gt;2 RBA screws&lt;/p&gt;&lt;p&gt;2 cover screws&lt;/p&gt;&lt;p&gt;1 screwdriver&lt;/p&gt;&lt;p&gt;1 Japaneses cotton sheet&lt;/p&gt;&lt;p&gt;1 USB charger&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;This kit features both Kangertech\'s KBOX mini and Subtank Mini. These 2 different products dominated the market for their simple yet very functional design. The KBOX mini is a variable wattage device that can go up to 50 watts. This allows the KBOX mini to fire coils as low as 0.3 ohms - heaps of flavour and cloud production. The subtank mini, an industry-changing product, lets you choose between using cheap pre made coils (OCC) and building your own coils. This versatility has redefined the standards of tanks in the e-cigarette world.&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;Though labelled as a starter kit, the SUBOX kit requires extensive knowledge about e-cigarettes. Proper research is needed for safe usage.&amp;nbsp;&lt;/p&gt;','starter kit, subtank mini, kbox mini, kanger subox starter kit, aspire au, starter, e-cig starter, e-cigarette starter kit, variable wattage, vw, RBA, OCC, rebuildable','kevin','','');
